import {
  FETCH_WORDS_START,
  FETCH_WORDS_SUCCESS,
  FETCH_WORDS_FAILURE,
  ADD_ANSWER,
  CHECK_ANSWER
} from 'actionTypes'
import {
  fetchWords as fetchWordsApi,
} from 'api'

export const fetchWords = () => async dispatch => {
  dispatch({ type: FETCH_WORDS_START })

  try {
    const words = await fetchWordsApi()
    dispatch({
      type: FETCH_WORDS_SUCCESS,
      payload: words
    })
  } catch (err) {
    dispatch({
      type: FETCH_WORDS_FAILURE,
      payload: err,
      error: true
    })
  }
}

export const addAnswerAction = (id, answer) => dispatch => {
  dispatch({
    type: ADD_ANSWER,
    payload: {
      id: id,
      answer: answer
    }
  })
}
export const checkAnswer = () => dispatch => {
  dispatch({
    type: CHECK_ANSWER
  })
}
