export const getWords = state => {
  return state.words.words
}

export const getClassName = (state, ownProps) => {
  const indexItem = state.words.words.findIndex(item => item.id === ownProps.item.id)
  return state.words.words[indexItem].isCorrect
}

export const getProcents = state => {
  if (state.words.isUploaded) {
    let correct = 0
    for (let i = 0; i < state.words.words.length; i++) {
      if (state.words.words[i].isCorrect === 'correct') {
        correct++
      }
    }
    return correct
  }
}